document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll("pre > code").forEach(function(e){const t=document.createElement("button");t.className="copy-code-button",t.type="button",t.innerText="Copy",t.addEventListener("click",function(){navigator.clipboard.writeText(e.innerText).then(()=>{t.innerText="Copied!",setTimeout(()=>{t.innerText="Copy"},2e3)})});const n=e.parentNode;n.style.position="relative",n.parentNode.insertBefore(t,n)})})